mapper.update:
update type_of_exercise
set te_name = #{teName},
te_set = #{teSet},
te_reps = #{teReps},
te_tool = #{teTool}
where te_num = #{teNum}

mapper.select:
select username,password,authority from user where
username=#{username} and
password=#{password}

mapper.update:
update exercise_contents
set Exercise_reps =
#{ExerciseReps},
Exercise_lb = #{ExerciseLb}
where
Exercise_contents_code = #{ExerciseContentsCode}
